    private void Topping1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Topping1ActionPerformed
        // TODO add your handling code here:
         double price, labour, cost, rent, material, pepporoni, prinaple, chicken, vegtables;
        String Check = "";
        //The Entering Part
         cost = Double.parseDouble(Diameter.getText());
         
         labour = 1;
         rent = 1.5;
         material = 0.5 * cost;
         price = labour + rent + material;
         pepporoni = price + 1.5;
         
        Check = Topping1.getText();
         OutputLabel.setText("The Price of the Pizza is " + pepporoni + "0$");
    
    }//GEN-LAST:event_Topping1ActionPerformed

