    private void CalculateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CalculateActionPerformed
        // TODO add your handling code here:\ double labour, storeCost, Materials, cost;
        double price, labour, cost, rent, material, pepporoni, prinaple, chicken, vegtables;
        String Check = "";
        //The Entering Part
        cost = Double.parseDouble(Diameter.getText());

        labour = 1;
        rent = 1.5;
        material = 0.5 * cost;
        price = labour + rent + material;
        pepporoni = price + 1.5;

        OutputLabel.setText("The Price of the Pizza is $" + price + "0");

    }//GEN-LAST:event_CalculateActionPerformed

